import React from 'react';
import logo from './logo.svg';
import './App.css';
import { makeStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';
import Container from '@material-ui/core/Container';
import RandomGrid from './BoilerPlate.js'
import TextField from '@material-ui/core/TextField';

function App() {
  let grid = RandomGrid();
  let show = false;

  return (
    <div className="App">
      <Container maxWidth="md">
        <NestedGrid grid={grid}/>
      </Container>
      <form className={classes.root} noValidate autoComplete="off">
        <TextField id="standard-basic" label="Standard" />
        <TextField id="filled-basic" label="Filled" variant="filled" />
        <TextField id="outlined-basic" label="Outlined" variant="outlined" />
      </form>
      <button>
        Play
      </button>
    </div>
  );
}


const useStyles = makeStyles(theme => ({
  root: {
    flexGrow: 1,
  },
  paper: {
    padding: theme.spacing(1),
    textAlign: 'center',
    color: theme.palette.text.secondary,
  },
}));

function NestedGrid({grid}) {
  const classes = useStyles();

  function FormRow({arr}) {
    return (
      <React.Fragment>
        <Grid item xs={2}>
          <Paper className={classes.paper}>{arr[0]}</Paper>
        </Grid>
        <Grid item xs={2}>
          <Paper className={classes.paper}>{arr[1]}</Paper>
        </Grid>
        <Grid item xs={2}>
          <Paper className={classes.paper}>{arr[2]}</Paper>
        </Grid>
        <Grid item xs={2}>
          <Paper className={classes.paper}>{arr[3]}</Paper>
        </Grid>
      </React.Fragment>
    );
  }

  return (
    <div className={classes.root}>
      <Grid container justify="center" spacing={2}>
        <Grid container justify="center" item xs={8} spacing={2}>
          <FormRow arr={grid[0]}/>
        </Grid>
        <Grid container justify="center" item xs={8} spacing={2}>
          <FormRow arr={grid[1]}/>
        </Grid>
        <Grid container justify="center" item xs={8} spacing={2}>
          <FormRow arr={grid[2]}/>
        </Grid>
        <Grid container justify="center" item xs={8} spacing={2}>
          <FormRow arr={grid[3]}/>
        </Grid>
      </Grid>
    </div>
  );
}

export default App;
